import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL;

export const getChatHistory = (userId) => {
  return axios.get(`${API_URL}/api/history`, {
    headers: { 'x-user-id': userId }
  });
};

export const sendMessage = (userId, message, chatId) => {
  return axios.post(
    `${API_URL}/api/functions`,
    { question: message, style: "default" },
    {
      headers: { 'x-user-id': userId },
      params: { chatId }
    }
  );
};

export const loadChat = (userId, chatId) => {
  return axios.post(
    `${API_URL}/api/functions`,
    { action: "load_chat" },
    {
      headers: { 'x-user-id': userId },
      params: { chatId }
    }
  );
};
export const getChatTopics = (chatId) => {
  return axios.get(`${API_URL}/api/topics`, {
    params: { chatId }
  });
};

export const generateDocument = (topic, actionType) => {
  return axios.post(`${API_URL}/api/generate-document`, {
    topic,
    actionType
  });
};
