import React from 'react';
import { Button, Typography } from '@mui/material';
import { generateDocument } from '../../services/api';

const TopicActionsView = ({ topic, onBack }) => {
  const handleAction = async (actionType) => {
    try {
      // Llamar a la API correspondiente
      const result = await generateDocument(topic, actionType);
      // Manejar el resultado (podría abrir un modal o mostrar en el chat)
      console.log(result);
    } catch (error) {
      console.error("Error al generar documento:", error);
    }
  };

  return (
    <div className="actions-view">
      <Button 
        variant="outlined" 
        onClick={onBack}
        style={{ marginBottom: '16px' }}
      >
        Volver a temas
      </Button>

      <Typography variant="subtitle1" gutterBottom>
        Acciones para: {topic}
      </Typography>

      <div className="action-buttons">
        <Button 
          variant="contained" 
          fullWidth 
          onClick={() => handleAction('summary')}
          style={{ marginBottom: '8px' }}
        >
          Resumen del tema
        </Button>

        <Button 
          variant="contained" 
          fullWidth 
          onClick={() => handleAction('data-table')}
          style={{ marginBottom: '8px' }}
        >
          Tabla de datos
        </Button>

        <Button 
          variant="contained" 
          fullWidth 
          onClick={() => handleAction('sources')}
          style={{ marginBottom: '8px' }}
        >
          Fuentes para investigar
        </Button>

        <Button 
          variant="contained" 
          fullWidth 
          onClick={() => handleAction('document')}
          style={{ marginBottom: '8px' }}
          color="secondary"
        >
          Generar documento
        </Button>
      </div>
    </div>
  );
};

export default TopicActionsView;
