import React, { useState } from 'react';
import { Typography } from '@mui/material';
import HistoryIcon from '@mui/icons-material/History';
import ChatHistoryView from './ChatHistoryView';
import TopicsView from './TopicsView';
import TopicActionsView from './TopicActionsView';
import ModeSelector from './ModeSelector';
import NewChatButton from './NewChatButton';

const Sidebar = ({ chats, currentChatId, onSelectChat, onNewChat }) => {
  const [mode, setMode] = useState('history'); // 'history', 'topics', 'actions'
  const [selectedTopic, setSelectedTopic] = useState(null);

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <Typography variant="h6" style={{ marginBottom: '8px' }}>
          <HistoryIcon style={{ marginRight: 8, verticalAlign: 'middle' }} />
          {mode === 'history' && 'Historial de Chats'}
          {mode === 'topics' && 'Temas del Chat'}
          {mode === 'actions' && `Acciones: ${selectedTopic}`}
        </Typography>

        <ModeSelector currentMode={mode} onModeChange={setMode} />
      </div>

      <NewChatButton onClick={onNewChat} />

      {mode === 'history' && (
        <ChatHistoryView 
          chats={chats}
          currentChatId={currentChatId}
          onSelectChat={onSelectChat}
        />
      )}

      {mode === 'topics' && (
        <TopicsView 
          currentChatId={currentChatId}
          onTopicSelect={(topic) => {
            setSelectedTopic(topic);
            setMode('actions');
          }}
        />
      )}

      {mode === 'actions' && (
        <TopicActionsView 
          topic={selectedTopic}
          onBack={() => setMode('topics')}
        />
      )}
    </div>
  );
};

export default Sidebar;
